export var Loaders;
(function (Loaders) {
    Loaders.loadImage = async (path) => {
        return new Promise((resolve, reject) => {
            const img = new Image();
            img.src = path;
            img.addEventListener('load', () => {
                resolve(img);
            });
            img.addEventListener('error', () => {
                reject(`Image „${path}“ cannot be loaded.`);
            });
        });
    };
})(Loaders || (Loaders = {}));
